var readlineSync = require('readline-sync');

var num1= readlineSync.question("Please enter your first number ");
var num2 = readlineSync.question("Please enter your second number");

//var operation = readlineSync.question("Please enter the operation to perform: add, sub, mul, div  ");
var operations = ["add", "sub", "mul", "div"];
var index = readlineSync.keyInSelect(operations, 'Which operation?');

console.log(operations[index]);

var operationsInput = operations[index];

	if(operationsInput == "add") {
		var add = parseInt(num1) + parseInt(num2);
	console.log("The result is:  " +  add );
	}
		
	else if(operationsInput == "sub"){	
		var sub = parseInt(num1) - parseInt(num2);
		console.log("The result is:  " +  sub );
	}
		
	else if(operationsInput == "mul"){
		var mul = parseInt(num1) * parseInt(num2);
	console.log("The result is:  " +  mul );
		
	}
	else {
		var div = parseInt(num1) / parseInt(num2);
	console.log("The result is:  " +  div );	
   }
   
